import express, { type Request, Response, NextFunction } from "express";
import fs from "fs";
import path from "path";
import { fileURLToPath } from "url";
import { registerRoutes } from "./routes.js";

const __dirname = path.dirname(fileURLToPath(import.meta.url));

// Load JSON configuration files
let appConfig: any = {};
let destinations: any = {};
let services: any = {};

try {
  appConfig = JSON.parse(fs.readFileSync(path.join(__dirname, "../config/app.json"), "utf8"));
  destinations = JSON.parse(fs.readFileSync(path.join(__dirname, "../data/destinations.json"), "utf8"));
  services = JSON.parse(fs.readFileSync(path.join(__dirname, "../data/services.json"), "utf8"));
  console.log("‚úÖ Loaded JSON configuration files successfully");
} catch (error) {
  console.warn("‚ö†Ô∏è Could not load some JSON config files:", error);
}

const app = express();

// Configure CORS manually (temporarily until we add cors package)
app.use((req, res, next) => {
  res.header('Access-Control-Allow-Origin', process.env.FRONTEND_URL || 'http://localhost:5173');
  res.header('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
  res.header('Access-Control-Allow-Headers', 'Origin, X-Requested-With, Content-Type, Accept, Authorization');
  res.header('Access-Control-Allow-Credentials', 'true');

  if (req.method === 'OPTIONS') {
    res.sendStatus(200);
  } else {
    next();
  }
});

app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Logging middleware for API requests
app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      console.log(logLine);
    }
  });

  next();
});

// Health check endpoint
app.get("/health", (_req, res) => {
  res.json({ status: "ok", timestamp: new Date().toISOString() });
});

// API endpoints to serve JSON data
app.get("/api/destinations", (_req, res) => {
  res.json(destinations);
});

app.get("/api/services", (_req, res) => {
  res.json(services);
});

app.get("/api/config", (_req, res) => {
  res.json(appConfig);
});

// Serve API specification
app.get("/api/openapi.json", (_req, res) => {
  try {
    const apiSpec = JSON.parse(fs.readFileSync(path.join(__dirname, "../api-spec.json"), "utf8"));
    res.json(apiSpec);
  } catch (error) {
    res.status(404).json({ message: "API specification not found" });
  }
});

// Register other API routes
registerRoutes(app);

// Error handling middleware
app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
  const status = err.status || err.statusCode || 500;
  const message = err.message || "Internal Server Error";

  console.error(`Error ${status}: ${message}`, err);
  res.status(status).json({ message });
});

// Handle 404 for unknown routes
app.use("*", (_req, res) => {
  res.status(404).json({ message: "Route not found" });
});

// Start the server
const port = parseInt(process.env.PORT || '8080', 10);
app.listen(port, "0.0.0.0", () => {
  console.log(`Backend API server running on port ${port}`);
});

