frontend/src/components/Header.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Menu, X, ChevronDown } from "lucide-react";
import { Link, useLocation } from "wouter";
import logoImage from "@assets/Snowy-Spring-logo_1757438803888_1758027378266.png";

export default function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [location] = useLocation();

  const menuItems = [
    { name: "Home", href: "/" },
    {
      name: "Services", 
      href: "/services",
      submenu: [
        { name: "Flight Services", href: "/services/flights" },
        { name: "Hotel Bookings", href: "/services/hotels" },
        { name: "Visa Applications", href: "/services/visa" },
        { name: "Study Abroad", href: "/services/study-abroad" },
        { name: "Holiday Packages", href: "/services/holidays" },
        { name: "Travel Advisory", href: "/services/advisory" }
      ]
    },
    { name: "Tours", href: "/tours" },
    { name: "Blog", href: "/blog" },
    { name: "About", href: "/about" },
    { name: "Contact", href: "/contact" },
  ];

  const isActive = (href: string) => {
    if (href === "/") return location === "/";
    return location.startsWith(href);
  };

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-background/95 backdrop-blur-sm border-b">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link href="/" className="flex items-center space-x-2 hover:opacity-80 transition-opacity" data-testid="logo-link">
            <img src={logoImage} alt="SN.SP Limited" className="h-10 w-10" />
            <span className="text-xl font-bold text-foreground">SN.SP Limited</span>
          </Link>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex items-center space-x-8">
            {menuItems.map((item) => (
              item.submenu ? (
                <DropdownMenu key={item.name}>
                  <DropdownMenuTrigger asChild>
                    <button 
                      className={`flex items-center gap-1 transition-colors ${
                        isActive(item.href) ? 'text-primary' : 'text-foreground hover:text-primary'
                      }`}
                      data-testid={`nav-${item.name.toLowerCase()}`}
                    >
                      {item.name}
                      <ChevronDown className="h-4 w-4" />
                    </button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent>
                    {item.submenu.map((subItem) => (
                      <DropdownMenuItem key={subItem.name} asChild>
                        <Link href={subItem.href} className="cursor-pointer" data-testid={`nav-${subItem.name.toLowerCase().replace(" ", "-")}`}>
                          {subItem.name}
                        </Link>
                      </DropdownMenuItem>
                    ))}
                  </DropdownMenuContent>
                </DropdownMenu>
              ) : (
                <Link
                  key={item.name}
                  href={item.href}
                  className={`transition-colors ${
                    isActive(item.href) ? 'text-primary' : 'text-foreground hover:text-primary'
                  }`}
                  data-testid={`nav-${item.name.toLowerCase()}`}
                >
                  {item.name}
                </Link>
              )
            ))}
          </nav>

          {/* CTA Button */}
          <div className="hidden md:block">
            <Button asChild data-testid="button-get-quote">
              <Link href="/contact">Get a Quote</Link>
            </Button>
          </div>

          {/* Mobile Menu Button */}
          <Button
            variant="ghost"
            size="icon"
            className="md:hidden"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            data-testid="button-mobile-menu"
          >
            {isMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
          </Button>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="md:hidden py-4 border-t">
            <nav className="flex flex-col space-y-4">
              {menuItems.map((item) => (
                item.submenu ? (
                  <div key={item.name} className="space-y-2">
                    <div className="font-medium text-foreground px-2">{item.name}</div>
                    {item.submenu.map((subItem) => (
                      <Link
                        key={subItem.name}
                        href={subItem.href}
                        onClick={() => setIsMenuOpen(false)}
                        className={`block px-3 py-2 rounded-md text-base font-medium text-foreground hover:text-primary hover:bg-accent ${
                          isActive(subItem.href) ? 'text-primary' : ''
                        }`}
                        data-testid={`mobile-nav-${subItem.name.toLowerCase().replace(" ", "-")}`}
                      >
                        {subItem.name}
                      </Link>
                    ))}
                  </div>
                ) : (
                  <Link
                    key={item.name}
                    href={item.href}
                    onClick={() => setIsMenuOpen(false)}
                    className={`block px-3 py-2 rounded-md text-base font-medium text-foreground hover:text-primary hover:bg-accent ${
                      isActive(item.href) ? 'text-primary' : ''
                    }`}
                    data-testid={`mobile-nav-${item.name.toLowerCase()}`}
                  >
                    {item.name}
                  </Link>
                )
              ))}
              <Button asChild className="self-start" data-testid="button-mobile-quote">
                <Link href="/contact" onClick={() => setIsMenuOpen(false)}>Get a Quote</Link>
              </Button>
            </nav>
          </div>
        )}
      </div>
    </header>
  );
}

frontend/src/components/Hero.tsx.content:
import { Button } from "@/components/ui/button";
import { Plane, Quote } from "lucide-react";
import { useLocation } from "wouter";
import heroImage from "@assets/generated_images/Tropical_beach_hero_background_6df6d6fc.png";

export default function Hero() {
  const [, setLocation] = useLocation();

  const scrollToSection = (href: string) => {
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };

  const handleExploreServices = () => {
    // Navigate to services section or scroll to it
    const servicesElement = document.querySelector("#services");
    if (servicesElement) {
      servicesElement.scrollIntoView({ behavior: "smooth" });
    } else {
      // If not on home page, navigate to home then scroll
      setLocation("/#services");
    }
  };

  const handleGetQuote = () => {
    // Navigate to contact page
    setLocation("/contact");
  };

  return (
    <section id="home" className="relative min-h-screen flex items-center justify-center">
      {/* Background Image */}
      <div
        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
        style={{ backgroundImage: `url(${heroImage})` }}
      />

      {/* Dark Overlay for text readability */}
      <div className="absolute inset-0 bg-gradient-to-r from-black/60 via-black/40 to-black/30" />

      {/* Content */}
      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <div className="max-w-4xl mx-auto">
          <h1 className="text-4xl md:text-6xl font-bold text-white mb-6">
            Discover Your Next Adventure
          </h1>
          <p className="text-xl md:text-2xl text-white/90 mb-8 font-medium">
            Your Trusted Travel Partner for Unforgettable Journeys
          </p>
          <p className="text-lg text-white/80 mb-12 max-w-2xl mx-auto">
            Experience seamless travel planning with our expert team, tailored to your dream destinations.
          </p>

          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Button
              size="lg"
              onClick={handleExploreServices}
              className="bg-primary/90 backdrop-blur-sm hover:bg-primary text-white border border-primary-border"
              data-testid="button-explore-services"
            >
              <Plane className="mr-2 h-5 w-5" />
              Explore Services
            </Button>

            <Button
              size="lg"
              variant="outline"
              onClick={handleGetQuote}
              className="bg-white/10 backdrop-blur-sm border-white/30 text-white hover:bg-white/20"
              data-testid="button-hero-quote"
            >
              <Quote className="mr-2 h-5 w-5" />
              Get a Quote
            </Button>
          </div>
        </div>
      </div>

      {/* Scroll indicator */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
        <div className="w-6 h-10 border-2 border-white/50 rounded-full flex justify-center">
          <div className="w-1 h-3 bg-white/50 rounded-full mt-2"></div>
        </div>
      </div>
    </section>
  );
}

